import React, { useEffect, useState } from 'react'
import axios from 'axios'
import CardFilms from './CardFilms'



const PageFilms = (props) => {

    const test = 'Marathn'
    const [movies, setMovies] = useState([])

    useEffect(() => {
        getApi()
    }, [])


    const getApi = () => {
        axios.get('https://hackathon-wild-hackoween.herokuapp.com/movies')
            .then(response => setMovies(response.data.movies))
            console.log(movies)
        }

        return (

        <>
            { test =='Marathon' ? movies.filter((movie) => movie.title === 'Final_Destination' || movie.title === 'Final_Destination_2' || movie.title == 'Final_ Destnation_3' || movie.title == 'Final_ Destnation_4' || movie.title == 'Final_ Destnation_5' || movie.title == 'Scream' || movie.title == 'Scream_2' || movie.title == 'Scream_3' || movie.title == 'Scream_4' || movie.title == 'Paranormal_Activity' || movie.title == 'Paranormal_Activity_2' || movie.title == '[REC]' || movie.title == '[REC]_2' || movie.title == 'Saw' || movie.title == 'Saw_2' || movie.title == 'Saw_3' || movie.title == 'Saw_4' || movie.title == 'Saw_5' || movie.title == 'Saw_6' || movie.title == 'Saw_3D' || movie.title == 'The_Hill_Have_Eyes' || movie.title == 'The_Hill_Have_Eyes_2' || movie.title == 'Insidious' || movie.title == 'Insidious:_Chapter_2').map((movie) => <CardFilms title={movie.title} image={movie.posterUrl}/>) 
            : test == 'Sanguinaire' ? movies.filter((movie) => movie.title === 'Poltergeist').map((movie) =><CardFilms title={movie.title} image={movie.posterUrl}/> ) 
            :
            ''}

        </>
    )
}

export default PageFilms
